    username: string;
    activityData: {
        day: string;
        mood: number;
        energy: number;
    }[];
    goalOverview: {
        percentage: number;
        weightChange: string;
        caloriesChange: string;
        streak: number;
    };
    nutritionProgress: {
        proteins: { current: number; total: number };
        carbohydrates: { current: number; total: number };
        fats: { current: number; total: number };
