// src/components/shared/Header.tsx
import React from "react";
import "./Header.css"

const Header: React.FC = () => {
    const [active, setActiveButton] = useState("Главная");

    const handleButtonClick = (buttonName: string) => {
        setActiveButton(buttonName);
    }

    return (
        <header className="header">
            <div className="header-left">
                <div className="logo">
                    <span className="logo-white">Tr</span>
                    <span className="logo-red">Ai</span>
                </div>
                <div className="tagline">
                    your personal training intelligence
                </div>
            </div>

            <nav className="header-right">
                <button
                    className={`nav-button ${activeButton === "Главная" ? "active" : ""}`}
                    onClick={() => handleButtonClick("Главная")}
                >
                    Главная
                </button>
                <button
                    className={`nav-button ${activeButton === "О нас" ? "active" : ""}`}
                    onClick={() => handleButtonClick("О нас")}
                >
                    О нас
                </button>
                <button
                    className={`nav-button ${activeButton === "Услуги" ? "active" : ""}`}
                    onClick={() => handleButtonClick("Услуги")}
                >
                    Услуги
                </button>
                <button
                    className={`nav-button ${activeButton === "Контакты" ? "active" : ""}`}
                    onClick={() => handleButtonClick("Контакты")}
                >
                    Контакты
                </button>
            </nav>

        </header>
    );
};


export default Header;
