import React, { useState } from "react";
import "./Login.css";
import ConfirmIcon from "../../assets/icons/confirm.svg";
import New_Acc from "../../assets/icons/add_acc.svg";
import Header from "../shared/Header/Header";
import Footer from "../shared/Footer/Footer";

const Login: React.FC = () => {
    const [formData, setFormData] = useState({
        username: "",
        password: ""
    });

    const handleInputChange = (field: string, value: string) => {
        setFormData(prev => ({
            ...prev,
            [field]: value
        }));
    };

    const handleLogin = (e: React.FormEvent) => {
        e.preventDefault();
        // Логика входа
        console.log("Login data:", formData);
    };

    const handleCreateAccount = () => {
        // Переход на страницу регистрации
        window.location.href = "/register";
    };

    return (
        <div className="login-page">
            <Header />

            <div className="login-container">
                <div className="login-box">
                    <h1 className="login-title">Authorization</h1>

                    <form onSubmit={handleLogin}>
                        <div className="form-section">
                            <h2 className="section-label">Username:</h2>
                            <input
                                type="text"
                                className="login-input"
                                placeholder="Имя пользователя"
                                value={formData.username}
                                onChange={(e) => handleInputChange("username", e.target.value)}
                            />
                        </div>

                        <div className="form-section">
                            <h2 className="section-label">Password:</h2>
                            <input
                                type="password"
                                className="login-input"
                                placeholder="Пароль"
                                value={formData.password}
                                onChange={(e) => handleInputChange("password", e.target.value)}
                            />
                        </div>

                        <div className="buttons-container">
                            <button type="submit" className="confirm-button">
                                <img src={ConfirmIcon} alt="Confirm" className="button-icon" />
                                Confirm
                            </button>

                            <button
                                type="button"
                                className="new-account-button"
                                onClick={handleCreateAccount}
                            >
                                <img src={New_Acc} alt="New account" className="button-icon" />
                                New account
                            </button>
                        </div>
                    </form>
                </div>
            </div>

            <Footer />
        </div>
    );
};

export default Login;
