import React, { useState } from "react";
import "./Login.css";
import ConfirmButton from "../shared/Buttons/ConfirmButton";
import New_Acc from "../../assets/icons/add_acc.svg";
import EyeOpen from "../../assets/icons/eye.svg";
import EyeClosed from "../../assets/icons/hide_eye.svg";
import Header from "../shared/Header/Header";
import Footer from "../shared/Footer/Footer";

const Login: React.FC = () => {
    const [formData, setFormData] = useState({
        username: "",
        password: ""
    });
    const [showPassword, setShowPassword] = useState(false);

    const togglePasswordVisibility = () => {
        setShowPassword(!showPassword);
    };

    const handleInputChange = (field: string, value: string) => {
        setFormData(prev => ({
            ...prev,
            [field]: value
        }));
    };

    const handleLogin = () => {
        // Логика входа
        console.log("Login data:", formData);
    };

    const handleCreateAccount = () => {
        window.location.href = "/register";
    };

    return (
        <div className="login-page">
            <Header />

            <div className="login-container">
                <div className="login-box">
                    <h1 className="login-main-title">Authorization</h1>

                    <form className="login-form">
                        {/* Username Section */}
                        <div className="input-section">
                            <h3 className="input-title">Username:</h3>
                            <div className="input-wrapper">
                                <input
                                    type="text"
                                    className="login-input-field"
                                    placeholder="Имя пользователя"
                                    value={formData.username}
                                    onChange={(e) => handleInputChange("username", e.target.value)}
                                />
                            </div>
                        </div>

                        {/* Password Section */}
                        <div className="input-section">
                            <h3 className="input-title">Password:</h3>
                            <div className="input-wrapper password-input-wrapper">
                                <input
                                    type={showPassword ? "text" : "password"}
                                    className="login-input-field password-field"
                                    placeholder="Пароль"
                                    value={formData.password}
                                    onChange={(e) => handleInputChange("password", e.target.value)}
                                />
                                <button
                                    type="button"
                                    className="password-toggle-btn"
                                    onClick={togglePasswordVisibility}
                                >
                                    <img
                                        src={showPassword ? EyeClosed : EyeOpen}
                                        alt={showPassword ? "Hide password" : "Show password"}
                                        className="password-toggle-icon"
                                    />
                                </button>
                            </div>
                        </div>

                        {/* Buttons */}
                        <div className="login-buttons">
                            <ConfirmButton onClick={handleLogin} />

                            <button
                                type="button"
                                className="login-new-account-btn"
                                onClick={handleCreateAccount}
                            >
                                <img src={New_Acc} alt="New account" className="button-icon" />
                                New account
                            </button>
                        </div>
                    </form>
                </div>
            </div>

            <Footer />
        </div>
    );
};

export default Login;
