import React, { useState } from "react";
import "./Login.css";
import EyeOpen from "../../assets/icons/eye.svg";
import EyeClosed from "../../assets/icons/hide_eye.svg";
import ConfirmButton from "./shared/Buttons/ConfirmButton";
import Header from "../shared/Header/Header";
import Footer from "../shared/Footer/Footer";

const Login: React.FC = () => {
    const [showPassword, setShowPassword] = useState(false);
    const [formData, setFormData] = useState({
        username: "",
        password: ""
    });

    const togglePasswordVisibility = () => {
        setShowPassword(!showPassword);
    };

    const handleInputChange = (field: string, value: string) => {
        setFormData(prev => ({
            ...prev,
            [field]: value
        }));
    };

    const handleLogin = (e: React.FormEvent) => {
        e.preventDefault();
        // –õ–æ–≥–∏–∫–∞ –≤—Ö–æ–¥–∞
        console.log("Login data:", formData);
    };

    const handleCreateAccount = () => {
        // –ü–µ—Ä–µ—Ö–æ–¥ –Ω–∞ —Å—Ç—Ä–∞–Ω–∏—Ü—É —Ä–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏–∏
        window.location.href = "/register";
    };

    return (
        <div className="login-page">
            <Header />

            <div className="login-container">
                <div className="login-box">
                    <h2 className="login-title">Authorization</h2>

                    <form onSubmit={handleLogin}>
                        <div className="form-group">
                            <label className="input-label">Username:</label>
                            <input
                                type="text"
                                className="input-field"
                                placeholder="–ò–º—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è"
                                value={formData.username}
                                onChange={(e) => handleInputChange("username", e.target.value)}
                            />
                        </div>

                        <div className="form-group">
                            <label className="input-label">Password:</label>
                            <div className="password-input-container">
                                <input
                                    type={showPassword ? "text" : "password"}
                                    className="input-field password-field"
                                    placeholder="–ü–∞—Ä–æ–ª—å"
                                    value={formData.password}
                                    onChange={(e) => handleInputChange("password", e.target.value)}
                                />
                                <button
                                    type="button"
                                    className="password-toggle"
                                    onClick={togglePasswordVisibility}
                                >
                                    <img
                                        src={showPassword ? EyeClosed : EyeOpen}
                                        alt={showPassword ? "Hide password" : "Show password"}
                                        width="20"
                                        height="20"
                                    />
                                </button>
                            </div>
                        </div>

                        <div className="divider"></div>

                        <button type="submit" className="confirm-login-button">
                            {/* üìå –î–æ–±–∞–≤—å—Ç–µ –∏–∫–æ–Ω–∫—É confirm —Å—é–¥–∞ */}
                            {/* <img src={ConfirmIcon} alt="Confirm" className="button-icon" /> */}
                            üìå Confirm
                        </button>
                    </form>

                    <div className="create-account-section">
                        <button
                            className="create-account-button"
                            onClick={handleCreateAccount}
                        >
                            {/* üìç –î–æ–±–∞–≤—å—Ç–µ –∏–∫–æ–Ω–∫—É account —Å—é–¥–∞ */}
                            {/* <img src={NewAccountIcon} alt="New account" className="button-icon" /> */}
                            New account
                        </button>
                    </div>
                </div>
            </div>

            <Footer />
        </div>
    );
};

export default Login;
